import React from 'react';
import { Link, Redirect } from 'react-router-dom'
import { Form, Input } from '@rocketseat/unform'
import * as Yup from 'yup';
import { BotaoLink } from './styled'
import { Container, Content } from './styled'

const schema = Yup.object().shape({
  Telefone: Yup.string()
    .required('Telefone é obrigatória!'),
  password: Yup.string()
    .required('A senha é obrigatória!')
    .min(6, 'No minimo 6 carácter'),
})


function direcionar() {
  alert()
  return (<Redirect to="/Jogos" />)
}

class Login extends Component {
  constructor() {
    super()
    this.state = []
  }

  handleSubmit({ Telefone, password }) {
    console.log('opa')

    direcionar()
  }

  render() {
    return (
      <Container>
        <Content>
          <Form schema={schema} onSubmit={handleSubmit} >
            <Input class="input" name="Telefone" type="tel" placeholder="Numero Telefone" />
            <Input class="input" name="password" type="password" placeholder="Senha" />

            <button type="submit"  >

              <span>Acessar</span>

            </button>

            <Link to="/register">Criar Conta!</Link>

          </Form>
        </Content>
      </Container>

    )
  }
}

export default Login;
