import styled from 'styled-components';

export const Container = styled.div`
 max-height: 40px;
  padding: 0 30px;
  max-width: 250px;
  background: #fff;
  display: right;
  align-items: left;
  border-radius: 0 20px ;
  border-color: #fff;
  background: #1C1C1C;
  margin: 5px;
  padding: 5px ;
  box-shadow: 0px 0px 30px #000;
  float: right;
.icone {
  cursor: pointer;
  margin-top: 5px;

}
.icone2 {
  &:hover{
    transition: background .7s;
    background: #696969;
    box-shadow: 0px 0px 10px #696969;
  }
  border-radius: 10px ;
  padding: 3px;

}

div#Icones {
  position:relative;
  max-height: 240px;
  width: 45px;
  height: 100%;
  border-radius: 0 20px ;
  border-color: #fff;
  background: #1C1C1C;
  margin: 5px;
  padding: 6px ;
  display: block;
  box-shadow: 0px 0px 30px #000;
}

`;
