import styled from 'styled-components';

export const Container = styled.div`

  overflow: hidden; /* para que não tenha rolagem se a imagem de fundo for maior que a tela */
  width: 100%;
  height: 100%;
  position: relative;
  background-image: linear-gradient(-20deg, #594F4F, #F67280);
  margin: 0;
  padding: 0;
  outline: 0;
  position: absolute;
  background-size: 100%;

 div#barraLateral {
  width: 50px;
  height: 100%;
  background-image: linear-gradient(-14deg, #59F, #474747);
  position: absolute;
  padding: 5px ;
  display: block;
 }

  h1#titulo {
    color: #fff;
  }
`
