import React from 'react';
import Hearder from '../../components/Header'

import { Container } from '../home/styled'
import BarraLateral from '../../components/BarraLateral'
import { Meio } from '../../components/CaixaCentral/styled';
import * as firebase from 'firebase'

export default class Pedidos extends Component {
  constructor() {
    super();
    this.state = {
      pedido: []
    }
  }


  componentDidMount() {
    var data = new Date();
    var dia = data.getDate()
    var mes = (data.getMonth() + 1)
    var ano = data.getFullYear()
    var cidade = 'Jaragua'

    const dadosPedidos = firebase.database().ref('Pedidos').child(cidade).child(ano).child(mes).child(dia)

    console.log('dadosPedidos', dadosPedidos)

    dadosPedidos.on('value', snap => {

      var pedido = snap.val()

      this.setState({

        pedido
      })
    })
  }


  renderObj = () => {
    const { pedido } = this.state
    console.log(pedido)
    return (
      Object.keys(pedido).map((obj, i) => {
        return (
          <div key={obj} class="pedido">
            <h1>Pedido</h1>
            <span class="negrito">Telefone: </span><span>{pedido[obj].telefoneCliente}</span>
            <p>
              <span class="negrito">Total: {valorFormatado(pedido[obj].totalPedido)} </span>
            </p>
            <p>
              <span class="negrito">Bairro: {pedido[obj].bairro} </span>
            </p>


            <button typw="button" onClick={() => gerarRelatorio(pedido[obj])} >

              <span>Imprimir Pedido</span>


            </button>
          </div>
        )
      }))
  }

  render() {

    return (

      <Container>
        <Container>
          <Hearder />
          <BarraLateral />
          <Meio>

          </Meio>
        </Container>
      </Container>

    )
  }
}
