import styled from 'styled-components';

export const Container = styled.div`
  padding: 5px;
  display: right;
  max-width: 60px;
  width: 100%;
  max-height: 240px;
  height:100%;
  align-items: left;
  display: flex;
  margin: 5px;





  float: left;

`;
export const Botao = styled.div`

.icone {
  cursor: pointer;
  margin-top: 5px;

}
.icone2 {
  &:hover{
    transition: background .7s;
    background: #696969;
    box-shadow: 0px 0px 10px #696969;
  }
  border-radius: 10px ;
  padding: 3px;

}

div#Icones {
  position:relative;
  max-height: 240px;
  width: 45px;
  height: 100%;
  border-radius: 0 20px ;
  border-color: #fff;
  background: #1C1C1C;
  margin: 5px;
  padding: 6px ;
  display: block;
  box-shadow: 0px 0px 30px #000;
}

`;
