import styled from 'styled-components';
import Fundo from '../../img/Fundo.jpg'

export const Container = styled.div`

  overflow: hidden; /* para que não tenha rolagem se a imagem de fundo for maior que a tela */
  width: 100%;
  height: 100%;
  position: relative;
  background-image: linear-gradient-(90deg, #594F4F, #363636);
  margin: 0;
  padding: 0;
  outline: 0;
  position: absolute;
  background-size: 100%;

 div#barraLateral {
  width: 50px;
  height: 100%;
  background-image: linear-gradient(-50deg, #FE7415, #474747);
  position: absolute;
 }

  h1#titulo {
    color: #fff;
  }
`
