      }

      span {
        flex:1;
        text-align: center;
        font-weight: bold;
      }
    }

    img {
    float: right;
    align-self: center;
    box-shadow: 0px 0px 10px #000;
    border-radius: 4px;
    width: 100%;
    border-radius: 4px;
    max-width: 300px;
    }

    .titulo {
        margin-top: 0px ;
        color: #fff;
    }

    .descricao {
        margin-left: 30px;
        margin-top: 20px ;
        color: #fff;
        width: 420px;
        text-align: justify;
    }

    .preco {

        color: #fff;
        font-size: 30px;

    }
`
