import styled from 'styled-components';

export const Container = styled.div`
  padding: 5px;

.icone {
  cursor: pointer;
  margin-top: 5px;

}

.icone2 {
  background: #000;
  border-radius: 20px ;
  padding: 3px;

}

div#Icones {
  position:relative;

  width: 45px;
  height: 100%;
  border-radius: 0 20px ;
  border-color: #fff;
  background: #1C1C1C;
  margin: 5px;
  padding: 6px ;
  display: block;
  box-shadow: 0px 0px 30px #000;
}



`;
